/**
 * @private
 */
import {_ajvRef} from "./_references";
import {RxVO} from "./rxvo";
import Ajv from "ajv";
import {default as JSONSchemaV4} from "../schemas/json-schema-draft-04";
import {default as JSONSchemaV6} from "../schemas/json-schema-draft-06";
import {default as OpenAPIv2} from "../../fixtures/OpenAPIv2";

const _validators = new WeakMap();

/**
 *
 * @param schema
 * @returns {string}
 */
const getSchemaID = (schema) => {
    const id = ["$id", "id"].filter((id) => schema.hasOwnProperty(id));
    return id.length ? schema[id[0]] : "root#";
};

/**
 *
 * @param {ajv.Ajv} _ajv
 * @param {AjvWrapper} inst
 * @param {object} schema
 * @returns {string}
 */
const createValidatorRef = (_ajv, inst, schema) => {
    const id = ["$id", "id"].filter((id) => schema.hasOwnProperty(id));
    let _schemaID;
    if (id) {
        _schemaID = schema[id[0]];
        _validators.get(inst)[_schemaID] = _ajv.getSchema(_schemaID);
    } else {
        _schemaID = schema["$id"] = "root#";
    }

    return _schemaID;
};
/**
 *
 * @param schemas
 * @param opts
 * @returns {ajv | ajv.Ajv}
 */
const createAjv = (inst, schemas, opts) => {
    const _ajv = new Ajv(opts);
    if (schemas && schemas !== null) {
        if (schemas.hasOwnProperty("meta")) {
            if (Array.isArray(schemas.meta)) {
                schemas.meta.forEach((meta) => {
                    _ajv.addMetaSchema(meta);
                });
            }
        }

        if (schemas.hasOwnProperty("schemas")) {
            let schemaID;
            if (Array.isArray(schemas.schemas)) {
                schemas.schemas.forEach((schema) => {
                    schemaID = getSchemaID(schema);
                    _ajv.addSchema(schema, schemaID);
                    // createValidatorRef(_ajv, inst, schema);
                });
                // sets last id as active schema;
                // _ajv.getSchema(schemaID);
            } else {
                if ((typeof schemas.schemas) === "string") {
                    schemaID = getSchemaID(schemas.schema);
                    _ajv.addSchema(schemas.schemas, schemaID);
                    // createValidatorRef(_ajv, inst, schema.schemas);
                    // _ajv.getSchema(schemaID);
                }
            }

            if (schemaID) {
                inst.path = schemaID;
            }
        }
    }

    return _ajv;
};

/**
 * Wrapper for Ajv JSON-PropertiesModel Validator
 * @private
 */
export class AjvWrapper {
    /**
     * @constructor
     * @param rxvo
     * @param schemas
     * @param ajvOptions
     */
    constructor(rxvo, schemas, ajvOptions = {}) {
        // ensures that we are given something that represents a RxVO object
        if ((!rxvo) || !(rxvo instanceof RxVO)) {
            throw "RxVO is required at arguments[0]";
        }

        if (_validators.get(this) === void(0)) {
            _validators.set(this, {});
        }

        // defines getter for parent RxVO reference
        Object.defineProperty(this, "$rxvo", {
            get: () => rxvo,
            enumerable: false,
        });

        // applies user specified options over our default Ajv Options
        const opts = Object.assign(_ajvOptions, ajvOptions);

        // makes user defined options object accessible for evaluation
        Object.defineProperty(this, "options", {
            get: () => opts,
            enumerable: true,
        });

        this.path = "root#";
        const _ajv = createAjv(this, schemas, opts);

        // initializes Ajv instance for this Doc and stores it to WeakMap
        _ajvRef.set(this, _ajv);

        // accept no further modifications to this object
        Object.seal(this);
    }

    /**
     * Helper method to derive path for given model
     * todo: review for removal
     * @param model
     * @return {string}
     */
    static resolvePath(model) {
        return "not yet implemented";
    }

    /**
     * Getter for captive Ajv validator
     * -- use this for Ajv API Methods
     * @returns {ajv}
     */
    get $ajv() {
        return _ajvRef.get(this);
    }

    /**
     * Executes validator at PropertiesModel $model `path` against `value`
     * @param {string} path
     * @param {boolean} value
     */
    exec(path, value) {
        // // appends id ref to path
        if (path.indexOf("#") < 0) {
            path = `${this.path}${path}`;
        }
        return this.$ajv.validate(path, value);
    }
}

/**
 * AJV Options Config in it's entirely for reference
 * only RxVO specific option changes are enabled
 * @type {*}
 * @private
 */
const _ajvOptions = {
    // // validation and reporting options:
    // $data:            false,
    // allErrors:        true,
    // verbose:          true,
    // $comment:         false, // NEW in Ajv version 6.0
    jsonPointers: true,
    // uniqueItems:      true,
    // unicode:          true,
    // format:           'fast',
    // formats:          {},
    // unknownFormats:   true,
    // schemas:          {},
    // logger:           undefined,
    // referenced schema options:
    schemaId: 'auto',
    // missingRefs:      true,
    extendRefs: true, // default 'ignore'
    // loadSchema:       undefined, // function(uri: string): Promise {}
    // options to modify validated data:
    // removeAdditional: true,
    // useDefaults: true,
    // coerceTypes:      false,
    // asynchronous validation options:
    // transpile:        undefined, // requires ajv-async package
    // advanced options:
    // meta:             true,
    // validateSchema:   true,
    // addUsedSchema:    true,
    // inlineRefs:       true,
    // passContext:      false,
    // loopRequired:     Infinity,
    // ownProperties:    false,
    // multipleOfPrecision: false,
    // errorDataPath:    'object', // deprecated
    // messages:         true,
    // sourceCode:       false,
    // processCode:      undefined, // function (str: string): string {}
    // cache:            new Cache,
    // serialize:        undefined
};

import {default as JSONSchemaV4} from "../schemas/json-schema-draft-04";
import {default as JSONSchemaV6} from "../schemas/json-schema-draft-06";
import {default as OpenAPIv2} from "../../fixtures/OpenAPIv2";
import {default as PetStoreV2} from "../../fixtures/petstore.v2";
import {default as BasicAPI} from "../../fixtures/basic-api.swagger";
import Ajv from "ajv";
import {AjvWrapper} from "./_ajvWrapper";
import {RxVO} from "./rxvo";

describe("AJVWrapper Tests", () => {
    describe("AJV Standalone -- version integrity & debug", () => {
        it("should handle v4 Schemas", () => {
            const opts = {
                schemaId: "auto",
                jsonPointers: true,
                allErrors: false,
                extendRefs: true,
            };
            const _ajv = new Ajv(opts);
            _ajv.addMetaSchema(JSONSchemaV4);
            _ajv.addMetaSchema(JSONSchemaV6);
            _ajv.addSchema(OpenAPIv2, "http://swagger.io/v2/schema.json#");
            // const _v = _ajv.getSchema("http://swagger.io/v2/schema.json");
            // const _isValid = _v(PetStoreV2);
            // expect(_isValid).toBe(true);
            expect(_ajv.validate("http://swagger.io/v2/schema.json#/", PetStoreV2)).toBe(true);

        });
    });

    describe("AJVWrapper", () => {
        it("should handle v4 Schemas", () => {
            const opts = {
                schemaId: "auto",
                jsonPointers: true,
                allErrors: false,
                extendRefs: true,
            };

            const schemas = {
                meta: [JSONSchemaV4, JSONSchemaV6],
                schemas: [OpenAPIv2],
                // use: "http://swagger.io/v2/schema.json#"
            };

            const rxvo = new RxVO(schemas);
            const _ajv = new AjvWrapper(rxvo, schemas, opts);
            const _isValid = _ajv.exec("http://swagger.io/v2/schema.json#", PetStoreV2);

            rxvo.model = PetStoreV2;

            expect(rxvo.errors).toBe(null);
            expect(_isValid).toBe(true);
        });
    });
});

import {_oBuilders} from "./_references";
const notifiers = new WeakMap();

/**
 * protocol for Errors
 */
class ErrorNotification {
    /**
     * @constructor
     * @param {string} path
     * @param {string|string[]} error
     */
    constructor(path, error) {
        // defines getter for `path` prop
        Object.defineProperty(this, "path", {
            get: () => path,
            enumerable: true,
        });
        // defines getter for `error` prop
        Object.defineProperty(this, "error", {
            get: () => error,
            enumerable: true,
        });
    }

    toString() {
        return `"${this.path}": ${JSON.stringify(this.error)}`;
    }
}

/**
 * todo: implement this somewhere
 */
class CompleteNotification {
    /**
     * @constructor
     * @param {string} path
     */
    constructor(path) {
        Object.defineProperty(this, "path", {
            get: () => path,
            enumerable: true,
        });
    }
}

/**
 * Notification Management
 * acts as liason between Models and RxJS Subscription Dispatchers
 */
class Notifier {
    /**
     *
     * @param rxvo
     * @returns {Notifier|*}
     */
    constructor(rxvo) {
        Object.defineProperty(this, "$rxvo", {
            get: () => rxvo,
        });

        notifiers.set(rxvo, this);
    }

    /**
     * Delegates sending RxJS `next` notifications
     * @param forPath
     */
    sendNext(forPath) {
        // this is a kludge to "wait a tic" before sending the notifications
        setTimeout(() => {
            if (forPath[0] !== ".") {
                forPath = `.${forPath}`;
            }

            const _models = this.$rxvo.getModelsInPath(forPath);

            if (_models[0] !== "") {
                _models.splice(0, 0, "")
            }

            _models.forEach((model) => {
                _oBuilders.get(this.$rxvo).next(model.$model);
            });
        }, 0);
    }

    /**
     * Delegates sending RxJS `error` notifications
     * @param forPath
     * @param error
     */
    sendError(forPath, error) {
        const _models = this.$rxvo.getModelsInPath(forPath);
        _models.forEach((model) => {
            _oBuilders.get(this.$rxvo).error(model.$model,
                new ErrorNotification(model.$model.path, error));
        });
    }

    /**
     * Delegates sending RxJS `complete` notifications
     * @param forPath
     */
    sendComplete(forPath) {
        const _models = this.$rxvo.getModelsInPath(forPath);
        _models.forEach((model) => {
            _oBuilders.get(this.$rxvo).complete(model.$model);
        });
    }
}

/**
 * Creates and Retrives Notification Handlers
 * @static
 */
export default class Notifiers {
    /**
     *
     * @param rxvo
     * @returns {*}
     */
    static create(rxvo) {
       new Notifier(rxvo);
       return Notifiers.get(rxvo);
    }

    /**
     *
     * @param rxvo
     * @returns {Notifier}
     */
    static get(rxvo) {
        return notifiers.get(rxvo);
    }
}

import {wf, _mdRef} from "./_references";
const _mData = new WeakMap();
/**
 * @private
 */
export class MetaData {
    /**
     * @constructor
     * @param {PropertiesModel|Set} _oRef -- Object Reference to item being described
     * @param {object} _data -- Initial Data {parent:PropertiesModel|ItemsModel}
     */
    constructor(_oRef, _data = {}) {
        let _cName = wf.Fun.getConstructorName(_oRef);

        if (this._createID == null) {
            let _id = 0;
            MetaData.prototype._createID = function () {
                if (this.__objID == null) {
                    _id = _id + 1;
                    this.__objID = `${_cName}${_id}`;
                }
                return this.__objID;
            };
        }

        _data = Object.assign({}, _data, {
            _id: this._createID(),
            _className: _cName,
            _created: Date.now()
        });

        _mData.set(this, _data);
        _mdRef.set(this, this);
    }

    /**
     * @param {string} key
     */
    get(key) {
        let __ = _mData.get(this);
        return __.hasOwnProperty(key) ? __[key] : null;
    }

    /**
     * not implemented
     */
    set() {
        return this;
    }

    /**
     * UUID of element
     * @returns {string} Unique ObjectID
     */
    get objectID() {
        return this.get("_id");
    }

    /**
     * Root PropertiesModel element
     * @returns {PropertiesModel|Set}
     */
    get root() {
        return this.get("_root");
    }

    /**
     * Path to element
     * @returns {string}
     */
    get path() {
        return this.get("_path");
    }

    /**
     * Owner RxVO document
     * @returns {RxVO}
     */
    get rxvo() {
        return this.get("_rxvo");
    }

    /**
     * Getter for parent model
     * @returns {string | null}
     */
    get parent() {
        return this.get("_parent");
    }

    /**
     * Provides representation of Model as JSON string
     * @return {string}
     */
    toString() {
        return JSON.stringify(_mData.get(this));
    }
}

import {MetaData} from "./_metaData";
import {PropertiesModel} from "./propertiesModel";
import {RxVO} from "./rxvo";
import {basicModel} from "../../fixtures/PropertiesModel.schemas";
describe("MetaData Units", () => {
    let _md = null;
    describe( "SubClass Validation", ()=> {
        it( "should accept objects that are subclasses of PropertiesModel", function() {
            let _ = new PropertiesModel(new RxVO(basicModel));
            !expect(() => _md = new MetaData(_, {_path: "", _root: _})).not.toThrow(
                "new MetaData() argument 1 requires subclass PropertiesModel or ItemsModel. Was subclass of '<Object>'");
        });
        it( "should accept objects that are subclasses of ItemsModel", function() {
            let _ = new Set();
            expect(()=> _md = new MetaData( _, { _path: "", _root: _})).not.toThrow(
                "new MetaData() argument 1 requires subclass PropertiesModel or ItemsModel. Was subclass of '<Object>'"
            );
        });
    });
    describe( "Parameter Accessor Validation", ()=> {
        const rxvo = new RxVO(basicModel);
        let _ = rxvo.model.$model;
        const _md = new MetaData( new PropertiesModel(rxvo), {_path: "key", _root: _, _parent: _});
        it("should access `root` property", function() {
            expect(typeof _md.root).toBe("object");
        });
        it("should access `path` property", function() {
            expect(_md.path).toEqual("key");
        });
        it("should access `parent` property", function() {
            let _md = new MetaData( _, { _path: "", _root: _});
            expect(typeof _md.parent).toEqual("object");
        });
    });
});

import {_observers} from "./_references";
import {BehaviorSubject} from "rxjs/Rx";

const _observerPaths = new WeakMap();

export class ObserverBuilder {
    /**
     * @constructor
     */
    constructor() {
        _observers.set(this, new WeakMap());
        _observerPaths.set(this, []);
    }

    /**
     * Retrieves BehaviorSubject Collection for given Model
     * @param target {Model}
     * @returns {Observer|null} Observer at path reference
     */
    get(target) {
        return _observers.get(this).get(target) || null;
    }

    /**
     * Retrieves BehaviorSubject Collection for Model at given path
     * @param path
     * @returns {*}
     */
    getObserverForPath(path) {
        const _itm = _observerPaths.get(this).find((o) => o[0] === `${path}`);
        return _itm && _itm.length > 1 ? this.get(_itm[1]) : null;
    }

    list() {
        return _observerPaths.get(this).map((o) => o[0]);
    }

    /**
     * Creates BehaviorSubjects for given Model
     * @param target {Model}
     */
    create(target) {
        const _o = _observers.get(this);
        const _h = {
            onNext: new BehaviorSubject(null).skip(1),
            onError: new BehaviorSubject(null).skip(1),
            onComplete: new BehaviorSubject(null).skip(1),
        };
        _o.set(target, _h);
        _observerPaths.get(this).splice(-1, 0, [`${target.path}`, target]);
        return _o.get(target);
    }

    /**
     * Calls next on Next Subject
     * @param target {Model}
     */
    next(target) {
        if (!target || target.isDirty) {
            return;
        }

        let _o = this.get(target);
        if (_o !== null) {
            _o.onNext.next(target);
        }
    }

    /**
     * Calls next on Complete Subject
     * @param target {Model}
     */
    complete(target) {
        let _o = this.get(target);
        if (_o !== null) {
            _o.onComplete.next(target);
        }
    }

    /**
     * Calls next on Error Subject
     * @param target {Model}
     * @param message {*}
     */
    error(target, message) {
        let _o = this.get(target);
        if (_o !== null) {
            _o.onError.next(message);
        }
    }
}

