    beforeEach(() => {
        _rxvo = new RxVO(basicModel);
        _schema = new PropertiesModel(_rxvo);
        _sH = new SchemaHelpers(_schema);
    });

    describe("Child Object Methods", () => {
        beforeEach(() => {
            _rxvo = new RxVO({schemas: [basicModel]});
            _schema = new PropertiesModel(_rxvo);
            _sH = new SchemaHelpers(_schema);
        });

        it("should set PropertiesModel from Object values", () => {
            expect((typeof _sH.setObject({properties: {name: "bar"}})) === "string").toBe(false);
            expect(_schema.model.properties.name).toBe("bar");
        });

        describe("Create Object", () => {
            it("should create Child PropertiesModel Object", () => {
                let _sC = _sH.createSchemaChild("foo", {}, _mdRef.get(_schema));
                expect((typeof _sC) === "string").toBe(false);
                expect(_sC instanceof PropertiesModel).toBe(true);
            });
        });
        describe("ItemsModel Values", () => {
            it("should set Value Object on Child PropertiesModel", () => {
                const sH = new SchemaHelpers(_schema);
                const _child = sH.setChildObject("", {baz: 123});
                expect(typeof _child).toBe("object");
                expect(_child.baz).toBe(123);
            });
        });

    });
});

import {
    _object, _schemaHelpers
} from "./_references";
import {Model} from "./model";
import {SchemaHelpers} from "./_schemaHelpers";
import {makeClean, makeDirty, refAtKeyValidation, refValidation} from "./utils";
import Notifiers from "./_branchNotifier";
import merge from "lodash.merge";

const _observerDelegates = new WeakMap();

/**
 * @class ItemsModel
 */
export class ItemsModel extends Model {
    /**
     * @constructor
     */
    constructor() {
        super(arguments[0]);
        _schemaHelpers.set(this, new SchemaHelpers(this));
        _object.set(this, new Proxy(Model.createRef(this, []), this.handler));
    }

    /**
     * getter for object model
     */
    get model() {
        return _object.get(this);
    }

    /**
     * setter for object model
     * @param value
     */
    set model(value) {
        if (!Array.isArray(value) || this.isFrozen) {
            return false;
        }

        let _idx = 0;
        // value.forEach((itm) => {
        //     let _defaults = this.rxvo.getDefaultsForPath(this.jsonPath);
        //     if (Object.keys(_defaults).length) {
        //         value[_idx] = merge(_defaults, itm);
        //     }
        //     _idx++;
        // });

        if (refValidation(this, value) !== true) {
            Notifiers.get(this.rxvo).sendError(this.jsonPath, this.rxvo.errors);
            return false;
        }

        if (!this.isDirty) {
            // marks model as dirty to prevent cascading validation calls
            makeDirty(this);
        }

        _object.set(this, new Proxy(Model.createRef(this, []), this.handler));
        _observerDelegates.set(this, true);

        try {
            let cnt = 0;

            value.forEach((val) => {
                _object.get(this)[cnt++] = val;
            });

        } catch (e) {
            makeClean(this);
            Notifiers.get(this.rxvo).sendError(this.jsonPath, e);
            return false;
        }

        makeClean(this);
        if (!this.isDirty) {
            Notifiers.get(this.rxvo).sendNext(this.jsonPath);
            _observerDelegates.delete(this);
        }

        return true;
    }

    get handler() {
        return Object.assign(super.handler, {
            get: (t, idx) => {
                // TODO: review for removal
                // if (typeof idx === "symbol") {
                //     idx = `${String(idx)}`;
                // }

                if (idx === "length") {
                    return t.length;
                }

                if (idx in Array.prototype) {
                    // only handle methods that modify the reference array
                    if (["fill", "pop", "push", "shift", "splice", "unshift"].indexOf(idx) > -1) {
                        return applyMethod(this, t, idx);
                    } else {
                        return t[idx];
                    }
                }

                if (idx === "$model") {
                    return this;
                }

                return t[idx];
            },
            set: (t, idx, value) => {
                if (idx in Array.prototype) {
                    // do nothing against proto props
                    return true;
                }

                // -- ensures we aren't in a frozen hierarchy branch
                if (this.isFrozen) {
                    return false;
                }

                let _oDel = _observerDelegates.get(this);

                if (refAtKeyValidation(this, "items", value) !== true) {
                    if (_oDel !== void(0)) {
                        makeClean(this);
                        Notifiers.get(this.rxvo).sendError(this.jsonPath, this.rxvo.errors);
                    }
                    return false;
                }

                // we set the value on the array with success
                if ((typeof value) === "object") {
                    let _sH = _schemaHelpers.get(this);
                    value = _sH.setChildObject(`${this.path}`, value);
                }

                t[idx] = value;

                // makes clean if not serial operation
                if (_oDel !== void(0)) {
                    makeClean(this);
                    // updates observers
                    // Notifiers.get(this.rxvo).sendNext(this.jsonPath);
                }

                return true;
            },

            deleteProperty: (t, idx) => {
                return deleteTrap(this, t, idx);
            }
        });
    }
}

/**
 * Handles Proxy Delete Trap for Array elements
 * @param model
 * @param t
 * @param idx
 * @returns {boolean}
 */
const deleteTrap = (model, t, idx) => {
    let _oDel = _observerDelegates.get(model);
    // creates mock of future Model state for evaluation
    let _o = [].concat(t);
    try {
        // attempts splice method to
        // remove item at given index index
        _o.splice(idx, 1);
    } catch (e) {
        if (!_oDel) {
            makeClean(model);
            Notifiers.get(model.rxvo).sendError(model.jsonPath, e);
        }
        return false;
    }

    // validates mock of change state
    const _res = refValidation(model, _o);


    if (_res !== true) {
        // makes clean and notifies
        // if not serial operation
        if (!_oDel) {
            makeClean(model);
            Notifiers.get(model.rxvo).sendError(model.jsonPath, _res);
        }
        return false;
    }

    // applies operation
    t.splice(idx, 1);

    // flags model as in sync with tree
    makeClean(model);

    // updates observers
    Notifiers.get(model.rxvo).sendNext(model.jsonPath);
    return true;
};

/**
 * Handles proxy get for Array proto methods
 * @param model
 * @param t
 * @param idx
 * @returns {function(...[*]=)}
 */
const applyMethod = (model, t, idx) => {

    // returns closure analog to referenced method
    return (...args) => {
        // mocks current model state
        const _arr = [].concat(t);

        // applies method to model state
        const _val = t[idx].apply(_arr, args);

        // validates updated mock
        const _res = refValidation(model, _arr);

        // in event of validation failure
        if (_res !== true) {
            // .. marks model as clean
            makeClean(model);

            // .. sends notifications
            Notifiers.get(model.rxvo).sendError(model.jsonPath,
                model.rxvo.errors);

            return false;
        }

        // model is a kludge to handle updates from proto methods
        if (model.parent !== null) {
            // applies change to parent object if model is not root context
            model.parent.model[model.jsonPath.split(".").pop()] = _arr;
        } else {
            // applies change to RxVO instance
            model.rxvo.model = _arr;
        }

        return _val;
    }
};

import {RxVO} from "./rxvo";
import {default as deepEqual} from "deep-equal";
import {
    stringsCollection,
    stringsMinMaxCollection,
    objectCollection, objectCollectionDefaults
} from "../../fixtures/ItemsModel.schemas";
import {Model} from "./model";

describe("ItemsModel Class Suite", function () {

    describe("Simple ItemsModel Tests", () => {
        beforeEach(() => {
            this.rxvo = new RxVO({schemas: [stringsCollection]});
        });

        describe("LifeCycle: Instantiation", () => {
            it("should initialize a schema and a schema object", () => {
                expect(this.rxvo.model.$model).toBeDefined();
                expect(this.rxvo.model.$model instanceof Model).toBe(true);
            });

            it("should not initialize a invalid schema and schema object", () => {
                let badSchema = Object.assign({}, stringsCollection, {
                    items: [{type: "INVALID"}],
                });
                expect(() => new RxVO({schemas: [badSchema]})).toThrow();
            });
        });

        describe("LifeCycle: Creation", () => {

            let _d;

            it("should populate with valid data and make that data accessible", () => {
                _d = ["abc", "def", "ghi"];

                this.rxvo.model = _d;
                expect(deepEqual(this.rxvo.model, _d)).toBe(true);
            });

            it("should reject invalid data and leave model pristine", () => {
                _d = [99, 100, 101];

                this.rxvo.model = _d;
                expect(deepEqual(this.rxvo.model, {})).toBe(true);
            });
        });
    });

    describe("Nested Elements Tests", () => {
        beforeEach(() => {
            this.rxvo = new RxVO({schemas: [objectCollection]});
        });

        describe("LifeCycle: Instantiation", () => {
            it("should initialize a valid schema and a schema object", () => {
                expect(this.rxvo.model.$model).toBeDefined();
                expect(this.rxvo.model.$model instanceof Model).toBe(true);
                expect(this.rxvo.model.$model).toBeDefined();
                expect(this.rxvo.model.$model instanceof Model).toBe(true);
            });
        });

        describe("LifeCycle: Create", () => {

            let _d;

            it("should populate with valid data and make that data accessible", () => {
                _d = [{
                    name: "Item A",
                    value: 1,
                }, {
                    name: "Item B",
                }, {
                    name: "Item C",
                    value: 2,
                }];

                this.rxvo.model = _d;
                expect(deepEqual(this.rxvo.model, _d)).toBe(true);
            });

            it("should reject invalid data and leave model pristine", () => {
                _d = [{
                    name: 123,
                    value: 1,
                }, {
                    value: "Item B",
                }, {
                    value: 2,
                }];

                this.rxvo.model = _d;
                expect(typeof this.rxvo.errors).toBe("object");
                expect(deepEqual(this.rxvo.model, [])).toBe(true);
            });
        });

        describe("LifeCycle: Update", () => {

            let _d;

            it("should update nested models with valid data and pass validation", () => {
                _d = [{
                    name: "Item A",
                    value: 1,
                }, {
                    name: "Item B",
                }, {
                    name: "Item C",
                    value: 2,
                }];

                this.rxvo.model = _d;

                this.rxvo.model[1] = {
                    name: "Item B",
                    value: 3
                };

                expect(this.rxvo.errors).toBe(null);
                expect(this.rxvo.model[1]).toEqual({name: "Item B", value: 3});
            });

            it("should updated properties in nested objects with valid data and pass validation", () => {
                _d = [{
                    name: "Item A",
                    value: 1,
                }, {
                    name: "Item B",
                }, {
                    name: "Item C",
                    value: 2,
                }];

                this.rxvo.model = _d;

                this.rxvo.model[1].value = 3;


                expect(this.rxvo.errors).toBe(null);
                expect(this.rxvo.model[1]).toEqual({name: "Item B", value: 3});
            });
        });

        describe("LifeCycle: Delete", () => {
            beforeEach(() => {
                this.rxvo = new RxVO({schemas: [stringsMinMaxCollection]});
            });

            _d = ["Item A", "Item B", "Item C"];

            it("should allow deletion of nested properties that are not required", () => {
                this.rxvo.model = _d;
                delete this.rxvo.model[1];
                expect(this.rxvo.errors).toBe(null);
                expect(this.rxvo.model.length).toBe(2);
            });

            it("should prevent deletion of nested properties that are required", () => {
                this.rxvo.model = _d;
                delete this.rxvo.model[0];
                delete this.rxvo.model[1];
                delete this.rxvo.model[2];
                expect(typeof this.rxvo.errors).toBe("object");
                expect(this.rxvo.model.length).toBe(1);
            });
        });
    });

    describe("Array Prototype method tests", () => {
        beforeEach(() => {
            this.rxvo = new RxVO({schemas: [stringsMinMaxCollection]});
            this.rxvo.model = ["Item A", "Item B", "Item C"];
        });

        it("should fill with validation", () => {
            this.rxvo.model.fill(["Item A", "Item B", "Item C", "Item D"]);
            expect(typeof this.rxvo.errors).toBe("object");
            expect(this.rxvo.model.length).toBe(3);
        });

        it("should pop with validation", () => {
            this.rxvo.model.pop();
            this.rxvo.model.pop();
            this.rxvo.model.pop();
            expect(typeof this.rxvo.errors).toBe("object");
            expect(this.rxvo.model.length).toBe(1);
        });

        it("should push with validation", () => {
            this.rxvo.model.push("Item D");
            expect(typeof this.rxvo.errors).toBe("object");
            expect(this.rxvo.model.length).toBe(3);
            expect(this.rxvo.model[2]).toBe("Item C");
        });

        it("should shift with validation", () => {
            this.rxvo.model.shift();
            this.rxvo.model.shift();
            this.rxvo.model.shift();
            expect(typeof this.rxvo.errors).toBe("object");
            expect(this.rxvo.model.length).toBe(1);
        });

        it("should splice with validation", () => {
            // remove all..
            this.rxvo.model.splice(0, -1);
            expect(typeof this.rxvo.errors).toBe("object");
            expect(this.rxvo.model.length).toBe(3);
            // append element...
            this.rxvo.model.splice(-1, 0, "Item D");
            expect(typeof this.rxvo.errors).toBe("object");
            expect(this.rxvo.model.length).toBe(3);
        });

        it("should unshift with validation", () => {
            this.rxvo.model.unshift("Item Z");
            expect(typeof this.rxvo.errors).toBe("object");
            expect(this.rxvo.model.length).toBe(3);
        });
    });

    describe("Default Values", () => {
        it("should apply defaults to items", () => {
            this.rxvo = new RxVO({schemas: [objectCollectionDefaults]}, {ajvOptions: {useDefaults: true}});
            this.rxvo.model = [{}];
            expect(this.rxvo.model[0]).toEqual({name: "abc"});
        });
    });

    describe("Model Class methods ", () => {

        it("should not reset if it would invalidate model", () => {
            const _rxvo = new RxVO({schemas: [stringsMinMaxCollection]});
            _rxvo.model = ["Item A", "Item B", "Item C"];
            expect(_rxvo.model.length).toBe(3);
            _rxvo.model.$model.reset();
            expect(_rxvo.model.length).toBe(3);
        });

        it("should reset it's collection if allowed", () => {
            const _rxvo = new RxVO({schemas: [stringsCollection]});
            _rxvo.model = ["Item A", "Item B", "Item C"];
            _rxvo.model = ["Item A", "Item B", "Item C"];
            expect(_rxvo.model.length).toBe(3);
            _rxvo.model.$model.reset();
            expect(_rxvo.model.length).toBe(0);
        });

        it("should quietly validate data with the validate method", () => {
            const _rxvo = new RxVO({schemas: [stringsCollection]});
            expect(_rxvo.model.$model.validate([1, 2, 3])).toBe("data/0 should be string");
            expect(_rxvo.model.$model.validate(["1", "2", "3"])).toBe(true);
        });

        it("should freeze it's model", () => {
            const _rxvo = new RxVO({schemas: [stringsCollection]});
            _rxvo.model = ["Item A", "Item B", "Item C"];
            _rxvo.model.$model.freeze();
            expect(_rxvo.model.$model.isFrozen).toBe(true);
            _rxvo.model = ["1", "2", "3"];
            expect(deepEqual(_rxvo.model, ["Item A", "Item B", "Item C"])).toBe(true);
        });

        it("should freeze it's model hierarchy", () => {
            const _rxvo = new RxVO({schemas: [objectCollection]});
            const _orig = [{
                name: "My Name",
                active: true,
            }];

            _rxvo.model = _orig;
            _rxvo.model.$model.freeze();

            expect(_rxvo.model.$model.isFrozen).toBe(true);
            // should not allow array to be overriden
            _rxvo.model = [{
                name: "Your Name",
                active: false,
            }];
            expect(deepEqual(_rxvo.model, _orig)).toBe(true);
            // should not allow array item to be overriden
            _rxvo.model[0] = {
                name: "Your Name",
                active: false,
            };
            expect(deepEqual(_rxvo.model, _orig)).toBe(true);
            // should not set attributes on nested object properties
            _rxvo.model[0].name = "Other Name";
            expect(deepEqual(_rxvo.model, _orig)).toBe(true);
        });
    });
});

