        const _schemas = _schemaSignatures.get(this);
        _schemas.schemas.some((schema) => {
            if (schema.hasOwnProperty("$id")) {
                if (schema.$id === id) {
                    _schema = schema;
                    return true;
                }
            } else if (schema.hasOwnProperty("id")) {
                if (schema.id === id) {
                    _schema = schema;
                    return true;
                }
            }
            return false;
        });
        return _schema;
    }

    /**
     * Retrieves JSON-Schema element for given Path
     * @param path
     * @returns {any}
     */
    getSchemaForPath(path) {
