import {_dirtyModels, _validators} from "./_references";
import merge from "lodash.merge";

/**
 * flags `model` node as being out of sync with tree
 * @param {Model} model
 */
export const makeDirty = (model) => {
    _dirtyModels.get(model.rxvo)[model.path] = true;
};

/**
 * flags `model` node as being in sync with tree
 * @param {Model} model
 */
export const makeClean = (model) => {
    if (model.isDirty && _dirtyModels.get(model.rxvo)[model.path]) {
        delete _dirtyModels.get(model.rxvo)[model.path];
    }
};

/**
 * Performs model data validation against json-schema
 * @private
 * @param {Model} model
 * @param {json|array|boolean|number|string} value
 * @return {boolean|string|object[]}
 */
export const refValidation = (model, value) => {
    return refAtKeyValidation(model, "", value);
};

/**
 * Performs model data validation at KEY against json-schema
 * @param model
 * @param key
 * @param value
 * @return {boolean|string|string[]}
 */
export const refAtKeyValidation = (model, key, value) => {
    // we don't validate if model is dirty
    if (model.isDirty) {
        return true;
    }

    // obtains preliminary model path
    let path = `${model.validationPath}`;

    // appends key to path if set
    if (key && key.length) {
        path = `${path}/${key}`;
    }

    const _res = validate(model, path, value);

    // tests our results for failure
    if (_res !== true) {
        return _res;
    }

    return true;
};

export const validate = (model, path, value) => {
    // obtains validator reference
    const _v = _validators.get(model.rxvo);
    const _res = _v.exec(path, value);
    // runs validation and returns
    if (_res !== true) {
        return _v.$ajv.errorsText(_res.errors) || "unknown validation error";
    }

    return true;
};

/**
 *
 * @param obj
 */
const getDefaultsForElement = (obj) => {
    let _o = {};
    const _propObj = {};

    if (obj.hasOwnProperty("default")) {
        merge(_o, obj.default);
    }

    if (obj.hasOwnProperty("type")) {
        let _m = obj.type.match(/^(object|array)+$/);
        if (_m !== null) {
            let _key = _m[1] === "object" ? "properties" : "items";
            if (obj.hasOwnProperty(_key)) {
                return merge(_o, getDefaultsForElement(obj[_key]));
            }
        }
    }

    Object.keys(obj).forEach((prop) => {
        if (obj[prop].hasOwnProperty("default")) {
            _o[prop] = obj[prop].default;
        }

        if ((obj[prop].hasOwnProperty("type"))) {
            if (obj[prop].type.match(/^(object|array)+$/) !== null) {

                if (obj[prop].hasOwnProperty("properties")) {
                    _propObj[prop] = getDefaultsForElement(obj[prop].properties)
                }

                if (obj[prop].hasOwnProperty("items")) {
                    _propObj[prop] = getDefaultsForElement(obj[prop].items)
                }
            }
        }
    });

    _o = merge(_o, _propObj);

    delete _o["items"];
    return Object.keys(_o).length ? _o : null;
};

/**
 *
 * @param schema
 * @returns {object|null}
 */
export const getDefaults = (schema) => {
    const _o = {};

    if (schema.hasOwnProperty("default")) {
        merge(_o, schema.default);
    }

    if (schema.hasOwnProperty("properties")) {
        merge(_o, getDefaultsForElement(schema.properties));
    }

    if (schema.hasOwnProperty("items")) {
        merge(_o, getDefaultsForElement(schema.items));
    }

    delete _o["items"];

    return Object.keys(_o).length ? _o : null;
};

/**
 *
 * @param schema
 * @returns {*}
 */
export const getPatternPropertyDefaults = (schema) => {
    let _o = {};

    if (schema === null || schema === void(0)) {
        return null;
    }

    if (schema.hasOwnProperty("patternProperties")) {
        let _propObj = {};
        Object.keys(schema.patternProperties).forEach((prop) => {
            merge(_o, getDefaultsForElement(schema.patternProperties));
        });
        merge(_o, _propObj);
    }

    return  Object.keys(_o).length ? _o : null;
};

/**
 * Navigates given object by path
 * @param path
 * @param toWalk
 * @param delimiter
 * @returns {{} & any}
 */
export const walkObject = (path, toWalk, delimiter = "/") => {
    let _s = Object.assign({}, toWalk);
    path.split(delimiter).forEach((part) => {
        if (part !== "") {
            _s = _s[part];
        }
    });
    return _s;
};

import {
    makeClean, makeDirty, getDefaults,
    getPatternPropertyDefaults, validate,
    refValidation, refAtKeyValidation, walkObject
} from "./utils";

import {
    basicModel, nestedModel,
    patternModel, nestedPatternModel
} from "../../fixtures/PropertiesModel.schemas";

import {RxVO} from "./rxvo";

describe("Utils tests", () => {
    describe("make dirty and make clean", () => {
        describe("basic PropertiesModel dirtiness", () => {
            const _rxvo = new RxVO(basicModel);
            it("should make model dirty", () => {
                makeDirty(_rxvo.model.$model);
                expect(_rxvo.model.$model.isDirty).toBe(true);
            });

            it("should make model clean", () => {
                makeClean(_rxvo.model.$model);
                expect(_rxvo.model.$model.isDirty).toBe(false);
            });
        });

        describe("nested PropertiesModel dirtiness", () => {
            const _rxvo = new RxVO({schemas: [nestedModel]});
            _rxvo.model = {
                aObject: {
                    bObject: {
                        bValue: 123,
                    },
                },
            };
            it("should make model dirty", () => {
                makeDirty(_rxvo.model.aObject.bObject.$model);
                expect(_rxvo.model.aObject.$model.isDirty).toBe(false);
                expect(_rxvo.model.aObject.bObject.$model.isDirty).toBe(true);
            });

            it("should make model clean", () => {
                makeClean(_rxvo.model.aObject.bObject.$model);
                expect(_rxvo.model.aObject.bObject.$model.isDirty).toBe(false);
            });
        });
    });

    describe("Validation Methods", () => {
        describe("refValidation tests", () => {
            const _rxvo = new RxVO({schemas: [basicModel]});

            it("should pass validation against refValidation", () => {
                const _res = refValidation(_rxvo.model.$model,
                    {name: "test", age: 99, active: true});
                expect(_res).toBe(true);
            });

            it("should fail validation against refValidation", () => {
                const _res = refValidation(_rxvo.model.$model,
                    {name: "test", age: "99", active: true});
                expect(_res).toBe("data/age should be number");
            });
        });

        describe("refAtKeyValidation tests", () => {
            const _rxvo = new RxVO({schemas: [nestedModel]});
            const _data = {
                aObject: {
                    bObject: {
                        bValue: 123,
                    },
                },
            };
            _rxvo.model = _data;
            it("should pass validation against refValidation", () => {
                const _res = refAtKeyValidation(_rxvo.model.aObject.$model,
                    "properties/bObject", _data.aObject.bObject);
                expect(_res).toBe(true);
            });

            it("should fail validation against refValidation", () => {
                _data.aObject.bObject.bValue = "123";
                const _res = refAtKeyValidation(_rxvo.model.aObject.$model,
                    "properties/bObject", _data.aObject.bObject);
                expect(_res).toBe("data/bValue should be integer");
            });
        });

        describe("validate method", () => {
            const _rxvo = new RxVO({schemas: [basicModel]});
            const _data = {name: "test", age: 99, active: true};
            it("should pass validation against refValidation", () => {
                const _res = validate(_rxvo.model.$model, "", _data);
                expect(_res).toBe(true);
            });

            it("should fail validation against refValidation", () => {
                _data.age = "99"
                const _res = validate(_rxvo.model.$model, "", _data);
                expect(_res).toBe("data/age should be number");
            });
        });
    });

    describe("getDefaults tests", () => {
        it("should getDefaults from schema", () => {
            const _schema = {
                type: "object",
                properties: {
                    name: {
                        type: "string",
                        default: "a name",
                    },
                },
            };
            expect(getDefaults(_schema)).toEqual({name: "a name"});
        });

        it("should getDefaults from PatternProperty schema", () => {
            const _schema = {
                type: "object",
                patternProperties: {
                    ".*": {
                        type: "object",
                        default: {
                            value: "abcd",
                        },
                    },
                },
            };
            expect(getDefaults(nestedPatternModel)).toEqual(null);
        });
    });

    describe("getPatternPropertyDefaults tests", () => {
        it("should get pattern properties for basic Properties element", () => {
            expect(getPatternPropertyDefaults(patternModel))
                .toEqual({"[^name]": {"value": "default value"}});
        });

        it("should get pattern properties for Nested Properties element", () => {
            expect(getPatternPropertyDefaults(nestedPatternModel)).toEqual(null);
            expect(getPatternPropertyDefaults(nestedPatternModel.properties.nested))
                .toEqual({".*": {"value": "default value"}});
        })
    });

    describe("walkObject test", () => {
        it("should walk object", () => {
            const _obj = {a: {b: {c: {val: "string"}}}};
            expect(walkObject("a/b/c", _obj)).toEqual({val: "string"});
            // with delimiter
            expect(walkObject("a.b.c", _obj, ".")).toEqual({val: "string"});
        })
    })
});

