import {wf, _mdRef} from "./_references";
const _mData = new WeakMap();
/**
 * @private
 */
export class MetaData {
    /**
     * @constructor
     * @param {PropertiesModel|Set} _oRef -- Object Reference to item being described
     * @param {object} _data -- Initial Data {parent:PropertiesModel|ItemsModel}
     */
    constructor(_oRef, _data = {}) {
        console.log(_oRef);
        let _cName = wf.Fun.getConstructorName(_oRef);

        if (this._createID == null) {
            let _id = 0;
            MetaData.prototype._createID = function () {
                if (this.__objID == null) {
                    _id = _id + 1;
                    this.__objID = `${_cName}${_id}`;
                }
                return this.__objID;
            };
        }

        _data = Object.assign({}, _data, {
            _id: this._createID(),
            _className: _cName,
            _created: Date.now()
        });

        _mData.set(this, _data);
        _mdRef.set(this, this);
    }

    /**
     * @param {string} key
     */
    get(key) {
        let __ = _mData.get(this);
        return __.hasOwnProperty(key) ? __[key] : null;
    }

    /**
     * not implemented
     */
    set() {
        return this;
    }

    /**
     * UUID of element
     * @returns {string} Unique ObjectID
     */
    get objectID() {
        return this.get("_id");
    }

    /**
     * Root PropertiesModel element
     * @returns {PropertiesModel|Set}
     */
    get root() {
        return this.get("_root");
    }

    /**
     * Path to element
     * @returns {string}
     */
    get path() {
        return this.get("_path");
    }

    /**
     * Owner Model document
     * @returns {Model}
     */
    get owner() {
        return this.get("_owner");
    }

    /**
     * Getter for parent model
     * @returns {string | null}
     */
    get parent() {
        return this.get("_parent");
    }

    /**
     * Provides representation of Model as JSON string
     * @return {string}
     */
    toString() {
        return JSON.stringify(_mData.get(this));
    }
}

import {MetaData} from "./_metaData";
import {PropertiesModel} from "./propertiesModel";
import {Model} from "./index";
import {basicModel} from "../../fixtures/PropertiesModel.schemas";
describe("MetaData Units", () => {
    let _md = null;
    describe( "SubClass Validation", ()=> {
        it( "should accept objects that are subclasses of PropertiesModel", function() {
            let _ = new PropertiesModel(new Model(basicModel));
            !expect(() => _md = new MetaData(_, {_path: "", _root: _})).not.toThrow(
                "new MetaData() argument 1 requires subclass PropertiesModel or ItemsModel. Was subclass of '<Object>'");
        });
        it( "should accept objects that are subclasses of ItemsModel", function() {
            let _ = new Set();
            expect(()=> _md = new MetaData( _, { _path: "", _root: _})).not.toThrow(
                "new MetaData() argument 1 requires subclass PropertiesModel or ItemsModel. Was subclass of '<Object>'"
            );
        });
    });
    describe( "Parameter Accessor Validation", ()=> {
        const owner = new Model(basicModel);
        let _ = owner.model.$model;
        const _md = new MetaData( new PropertiesModel(owner), {_path: "key", _root: _, _parent: _});
        it("should access `root` property", function() {
            expect(typeof _md.root).toBe("object");
        });
        it("should access `path` property", function() {
            expect(_md.path).toEqual("key");
        });
        it("should access `parent` property", function() {
            let _md = new MetaData( _, { _path: "", _root: _});
            expect(typeof _md.parent).toEqual("object");
        });
    });
});

