import {
    _object, _schemaHelpers, _oBuilders,
} from "./_references";
import {BaseModel} from "./base-model";
import {SchemaHelpers} from "./_schemaHelpers";
import {makeClean, makeDirty, refAtKeyValidation, refValidation} from "./utils";
import Notifiers from "./_branchNotifier";

const _observerDelegates = new WeakMap();

/**
 * @class ItemsModel
 */
export class ItemsModel extends BaseModel {
    /**
     * @constructor
     */
    constructor() {
        super(...arguments);
        _schemaHelpers.set(this, new SchemaHelpers(this));
        _object.set(this, new Proxy(BaseModel.createRef(this, []), this.handler));
    }

    /**
     * getter for object model
     */
    get model() {
        return _object.get(this);
    }

    /**
     * setter for object model
     * @param value
     */
    set model(value) {
        if (!Array.isArray(value)) {
            return `${this.path} invalid operation`;
        }

        if (this.isFrozen) {
            return false;
        }

        let _idx = 0;
        // value.forEach((itm) => {
        //     let _defaults = this.owner.getDefaultsForPath(this.jsonPath);
        //     if (Object.keys(_defaults).length) {
        //         value[_idx] = merge(_defaults, itm);
        //     }
        //     _idx++;
        // });

        if (refValidation(this, value) !== true) {
            Notifiers.get(this.owner).sendError(this.jsonPath, this.owner.errors);
            return `${JSON.stringify(this.owner.errors)}`;
        }

        if (!this.isDirty) {
            // marks model as dirty to prevent cascading validation calls
            makeDirty(this);
        }

        _oBuilders.get(this.owner).mute(this);

        _object.set(this, new Proxy(BaseModel.createRef(this, []), this.handler));
        _observerDelegates.set(this, true);

        try {
            let cnt = 0;

            value.forEach((val) => {
                _object.get(this)[cnt++] = val;
            });
            // todo: review why this wont fly
            // _object.get(this).splice(0,0, value);
        } catch (e) {
            makeClean(this);
            _oBuilders.get(this.owner).unmute(this);
            Notifiers.get(this.owner).sendError(this.jsonPath, e);
            return `${JSON.stringify(e)}`;
        }

        makeClean(this);

        if (!this.isDirty) {
            _oBuilders.get(this.owner).unmute(this);
            Notifiers.get(this.owner).sendNext(this.jsonPath);
            _observerDelegates.delete(this);
        }

        return true;
    }

    get handler() {
        return Object.assign(super.handler, {
            get: (t, idx) => {
                // TODO: review for removal
                // if (typeof idx === "symbol") {
                //     idx = `${String(idx)}`;
                // }

                if (idx === "length") {
                    return t.length;
                }

                if (idx in Array.prototype) {
                    // only handle methods that modify the reference array
                    if (["fill", "pop", "push", "shift", "splice", "unshift"].indexOf(idx) > -1) {
                        return applyMethod(this, t, idx);
                    } else {
                        return t[idx];
                    }
                }

                if (idx === "$model") {
                    return this;
                }

                return t[idx];
            },
            set: (t, idx, value) => {
                if (idx in Array.prototype) {
                    // do nothing against proto props
                    return true;
                }

                // -- ensures we aren't in a frozen hierarchy branch
                if (this.isFrozen) {
                    throw `model path "${this.path.length ? this.path : "."}" is non-configurable and non-writable`;
                }

                let _oDel = _observerDelegates.get(this);

                if (refAtKeyValidation(this, "items", value) !== true) {
                    if (_oDel !== void(0)) {
                        makeClean(this);
                        Notifiers.get(this.owner).sendError(this.jsonPath, this.owner.errors);
                    }
                    return `${JSON.stringify(this.owner.errors)}`;
                }

                // we set the value on the array with success
                if ((typeof value) === "object") {
                    let _sH = _schemaHelpers.get(this);
                    value = _sH.setChildObject(`${this.path}`, value);
                }

                t[idx] = value;

                // makes clean if not serial operation
                if (_oDel !== void(0)) {
                    makeClean(this);
                    // updates observers
                    // Notifiers.get(this.owner).sendNext(this.jsonPath);
                }

                return true;
            },

            deleteProperty: (t, idx) => {
                return deleteTrap(this, t, idx);
            }
        });
    }

    /**
     * Returns length of model array
     * @returns {number}
     */
    length() {
        return this.model.length();
    }
}

/**
 * Handles Proxy Delete Trap for Array elements
 * @param model
 * @param t
 * @param idx
 * @returns {boolean|string}
 */
const deleteTrap = (model, t, idx) => {
    let _oDel = _observerDelegates.get(model);
    // creates mock of future Model state for evaluation
    let _o = [].concat(t);
    try {
        // attempts splice method to
        // remove item at given index index
        _o.splice(idx, 1);
    } catch (e) {
        if (!_oDel) {
            makeClean(model);
            Notifiers.get(model.owner).sendError(model.jsonPath, e);
        }
        return `${JSON.stringify(e)}`;
    }

    // validates mock of change state
    const _res = refValidation(model, _o);


    if (_res !== true) {
        // makes clean and notifies
        // if not serial operation
        if (!_oDel) {
            makeClean(model);
            Notifiers.get(model.owner).sendError(model.jsonPath, _res);
        }
        return JSON.stringify(_res);
    }

    // applies operation
    t.splice(idx, 1);

    // flags model as in sync with tree
    makeClean(model);

    // updates observers
    Notifiers.get(model.owner).sendNext(model.jsonPath);
    return true;
};

/**
 * Handles proxy get for Array proto methods
 * @param model
 * @param t
 * @param idx
 * @returns {function(...[*]=)}
 */
const applyMethod = (model, t, idx) => {

    // returns closure analog to referenced method
    return (...args) => {
        // mocks current model state
        const _arr = [].concat(t);

        // applies method to model state
        const _val = t[idx].apply(_arr, args);

        // validates updated mock
        const _res = refValidation(model, _arr);

        // in event of validation failure
        if (_res !== true) {
            // .. marks model as clean
            makeClean(model);

            // .. sends notifications
            Notifiers.get(model.owner).sendError(model.jsonPath,
                model.owner.errors);

            return false;
        }

        // model is a kludge to handle updates from proto methods
        if (model.parent !== null) {
            // applies change to parent object if model is not root context
            model.parent.model[model.jsonPath.split(".").pop()] = _arr;
        } else {
            // applies change to Model instance
            model.owner.model = _arr;
        }

        return _val;
    }
};

import {Model} from "./index";
import {default as deepEqual} from "deep-equal";
import {
    stringsCollection,
    stringsMinMaxCollection,
    objectCollection, objectCollectionDefaults
} from "../../fixtures/ItemsModel.schemas";
import {BaseModel} from "./base-model";

describe("ItemsModel Class Suite", function () {

    describe("Simple ItemsModel Tests", () => {
        beforeEach(() => {
            this.owner = new Model({schemas: [stringsCollection]});
        });

        describe("LifeCycle: Instantiation", () => {
            it("should initialize a schema and a schema object", () => {
                expect(this.owner.model.$model).toBeDefined();
                expect(this.owner.model.$model instanceof BaseModel).toBe(true);
            });

            it("should not initialize a invalid schema and schema object", () => {
                let badSchema = Object.assign({}, stringsCollection, {
                    items: [{type: "INVALID"}],
                });
                expect(() => new Model({schemas: [badSchema]})).toThrow();
            });
        });

        describe("ItemsModel LifeCycle: Creation", () => {

            let _d;

            it("should populate with valid data and make that data accessible", (done) => {
                _d = ["abc", "def", "ghi"];
                let _cnt = 0;

                this.owner.subscribe({
                    next: (m) => {
                        _cnt++;
                        expect(deepEqual(this.owner.model, _d)).toBe(true);
                    },
                    error: done,
                });

                setTimeout(() => {
                    expect(_cnt).toEqual(1);
                    done();
                }, 100);

                this.owner.model = _d;
            });

            it("should reject invalid data and leave model pristine", () => {
                _d = [99, 100, 101];

                this.owner.model = _d;
                expect(deepEqual(this.owner.model, {})).toBe(true);
            });
        });
    });

    describe("Nested Elements Tests", () => {
        beforeEach(() => {
            this.owner = new Model({schemas: [objectCollection]});
        });

        describe("LifeCycle: Instantiation", () => {
            it("should initialize a valid schema and a schema object", () => {
                expect(this.owner.model.$model).toBeDefined();
                expect(this.owner.model.$model instanceof BaseModel).toBe(true);
                expect(this.owner.model.$model).toBeDefined();
                expect(this.owner.model.$model instanceof BaseModel).toBe(true);
            });
        });

        describe("ItemsModel LifeCycle: Nested Create", () => {

            let _d;

            it("should populate with valid data and make that data accessible", (done) => {
                _d = [{
                    name: "Item A",
                    value: 1,
                }, {
                    name: "Item B",
                }, {
                    name: "Item C",
                    value: 2,
                }];

                let _cnt = 0;

                this.owner.subscribe({
                    next: (m) => {
                        _cnt++;
                    },
                    error: done,
                });

                setTimeout(() => {
                    expect(_cnt).toEqual(1);
                    done();
                }, 100);

                this.owner.model = _d;
                // expect(deepEqual(this.owner.model, _d)).toBe(true);
            });

            it("should reject invalid data and leave model pristine", () => {
                _d = [{
                    name: 123,
                    value: 1,
                }, {
                    value: "Item B",
                }, {
                    value: 2,
                }];

                this.owner.model = _d;
                expect(typeof this.owner.errors).toBe("object");
                expect(deepEqual(this.owner.model, [])).toBe(true);
            });
        });

        describe("LifeCycle: Update", () => {

            let _d;

            it("should update nested models with valid data and pass validation", () => {
                _d = [{
                    name: "Item A",
                    value: 1,
                }, {
                    name: "Item B",
                }, {
                    name: "Item C",
                    value: 2,
                }];

                this.owner.model = _d;

                this.owner.model[1] = {
                    name: "Item B",
                    value: 3
                };

                expect(this.owner.errors).toBe(null);
                expect(this.owner.model[1]).toEqual({name: "Item B", value: 3});
            });

            it("should updated properties in nested objects with valid data and pass validation", () => {
                _d = [{
                    name: "Item A",
                    value: 1,
                }, {
                    name: "Item B",
                }, {
                    name: "Item C",
                    value: 2,
                }];

                this.owner.model = _d;

                this.owner.model[1].value = 3;


                expect(this.owner.errors).toBe(null);
                expect(this.owner.model[1]).toEqual({name: "Item B", value: 3});
            });
        });

        describe("LifeCycle: Delete", () => {
            beforeEach(() => {
                this.owner = new Model({schemas: [stringsMinMaxCollection]});
            });

            let _d = ["Item A", "Item B", "Item C"];

            it("should allow deletion of nested properties that are not required", () => {
                this.owner.model = _d;
                delete this.owner.model[1];
                expect(this.owner.errors).toBe(null);
                expect(this.owner.model.length).toBe(2);
            });

            it("should prevent deletion of nested properties that are required", () => {
                this.owner.model = _d;
                delete this.owner.model[0];
                delete this.owner.model[1];
                delete this.owner.model[2];
                expect(typeof this.owner.errors).toBe("object");
                expect(this.owner.model.length).toBe(1);
            });
        });

        describe("LifeCycle: Reset", () => {
