import {
    _object, _schemaHelpers,
} from "./_references";
import {
    makeClean, makeDirty,
    refAtKeyValidation, refValidation,
} from "./utils";
import {SchemaHelpers} from "./_schemaHelpers";
import {BaseModel} from "./base-model";
import Notifiers from "./_branchNotifier";

/**
 * @class PropertiesModel
 */
export class PropertiesModel extends BaseModel {
    /**
     *
     */
    constructor() {
        super(arguments[0]);

        // stores SchemaHelpers reference for use
        _schemaHelpers.set(this, new SchemaHelpers(this));

        // sets Proxy Model reference on map
        _object.set(this, new Proxy(BaseModel.createRef(this, {}), this.handler));
    }

    /**
     * Handler for Object Proxy Evaluation
     * @returns {{get: function, set: function}}
     */
    get handler() {
        return Object.assign(super.handler, {
            get: (t, key) => {
                return key === "$model" ? this : t[key];
            },
            set: (t, key, value) => {
                return setHandler(this, t, key, value);
            },
            deleteProperty: (t, key) => {
                return deleteHandler(this, t, key);
            }
        });
    }

    /**
     * getter for object model
     */
    get model() {
        return _object.get(this);
    }

    /**
     * setter for object model
     * @param value
     */
    set model(value) {
        // fails on attempts to set scalar value
        // or if this node is locked or fails validation
        if ((typeof value) !== "object" || this.isFrozen) {
            return false;
        }

        if (refValidation(this, value) !== true) {
            Notifiers.get(this.owner).sendError(this.jsonPath, this.owner.errors);
            return false;
        }

        if (!this.isDirty) {
            // marks model as dirty to prevent cascading validation calls
            makeDirty(this);
        }

        // defines new Proxy Object for data modeling
        _object.set(this,
            new Proxy(BaseModel.createRef(this, {}), this.handler));
        Object.keys(value).forEach((k) => {
            // -- added try/catch to avoid error in JSFiddle
            try {
                this.model[k] = value[k];
            } catch (e) {
                // marks model as clean
                makeClean(this);
                // sends notifications
                Notifiers.get(this.owner).sendError(this.jsonPath, e.message);
                return false;
            }
        });

        // marks model as in sync with tree
        makeClean(this);

        // // calls next's observable to update subscribers
        if (!this.isDirty) {
            Notifiers.get(this.owner).sendNext(this.jsonPath);
        }

        return true;
    }

    /**
     * @param {string} key
     * @returns {any}
     */
    get(key) {
        return this.model[key];
    }

    /**
     * sets value to schema key
     * @param {string|object} key
     * @param {any} value
     */
    set(key, value) {
        // attempts validation of value against schema
        if (!refAtKeyValidation(this, key, value)) {
            return false;
        }

        if (!this.isDirty) {
            // marks model as dirty to prevent cascading validation calls
            makeDirty(this);
        }

        // applies validated value to model
        this.model[key] = value;

        // updates observers
        Notifiers.get(this.owner).sendNext(this.jsonPath);

        // removes dirtiness
        makeClean(this);

        return this;
    }
}

/**
 * Negotiates Key values that are Objects
 * @param model
 * @param key
 * @returns {boolean}
 */
const handleObjectKey = (model, key) => {
    const _sH = _schemaHelpers.get(model);
    const e = _sH.setObject(key);
    if (typeof e === "string") {
        makeClean(model);
        Notifiers.get(model.owner).sendError(model.jsonPath, e);
        return false;
    }

    return true;
};

/**
 * Creates Model Child to set up Proxy Object
 * @param model
 * @param key
 * @param value
 * @returns {*}
 */
const createModelChild = (model, key, value) => {
    const _sH = _schemaHelpers.get(model);
    // calls validate with either full path if in PropertiesModel or key if nested in ItemsModel
    value = _sH.setChildObject(key, value);
    if ((typeof value) === "string") {
        // marks model as clean
        makeClean(model);
        // sends notifications
        Notifiers.get(model.owner).sendError(model.jsonPath, value);
        return false;
    }
    return value;
};

/**
 * Parameter Set trap for Proxy
 * @param model
 * @param t
 * @param key
 * @param value
 * @returns {boolean|string}
 */
const setHandler = (model, t, key, value) => {
    if (key in Object.prototype) {
        // do nothing against proto props
        return true;
    }

    // -- ensures we aren't in a frozen hierarchy branch
    if (model.isFrozen) {
        throw `model path "${model.path.length ? model.path : "."}" is non-configurable and non-writable`;
    }

    // checks for branch update status
    if (!model.isDirty) {
        let _o = Object.assign({}, t);
        _o[key] = value;
        // attempts validation of value update
        if (refValidation(model, _o) !== true) {
            makeClean(model);
            Notifiers.get(model.owner).sendError(model.jsonPath, model.owner.errors);
            return `${JSON.stringify(model.owner.errors)}`;
        }
    }

    // if key is type 'object', we will set directly
    if (typeof key === "object") {
        return handleObjectKey(model, key);
    }

    if ((typeof value) === "object") {
        if ((value = createModelChild(model, key, value)) === false) {
            return `${model.path} unable to create child object`;
        }
    }

    // performs the operation on Model
    t[key] = value;
    return true;
};


/**
 * Parameter Delete trap for Proxy
 * @param model
 * @param t
 * @param key
 * @returns {boolean}
 */
const deleteHandler = (model, t, key) => {
    // creates mock of future model state for evaluation
    let _o = Object.assign({}, model.model);
    delete _o[key];
    const _res = model.validate(_o);

    // validates model with value removed
    if (_res !== true) {
        Notifiers.get(model.owner).sendError(model.jsonPath, _res);
        return _res;
    }

    // performs delete operation on model
    delete t[key];
    return true;
};

import {PropertiesModel} from "./propertiesModel";
import {Model} from "./index";
import {default as deepEqual} from "deep-equal";
import {
    basicModel, nestedModel, nestedModelDefault, nestedPatternModel, patternModel
} from "../../fixtures/PropertiesModel.schemas";

describe("PropertiesModel Class Suite", function () {

    describe("Simple PropertiesModel Tests", () => {
        beforeEach(() => {
            this.owner = new Model({schemas: [basicModel]});
        });

        describe("LifeCycle: Instantiation", () => {
            it("should initialize a schema and a schema object", () => {
                expect(this.owner.model.$model).toBeDefined();
                expect(this.owner.model.$model instanceof PropertiesModel).toBe(true);
                expect(this.owner.model.$model).toBeDefined();
                expect(this.owner.model.$model instanceof PropertiesModel).toBe(true);
            });

            it("should not initialize a invalid schema and schema object", () => {
                let badSchema = Object.assign({}, basicModel, {
                    properties: {
                        "bad thing": {
                            type: "INVALID",
                        },
                    },
                });
                expect(() => new Model({schemas: [badSchema]})).toThrow();
            });
        });

        describe("LifeCycle: Creation", () => {

            let _d;

            it("should populate with valid data and make that data accessible", () => {
                _d = {
                    name: "Ed Testy",
                    age: 99,
                    active: true,
                };

                this.owner.model = _d;
                expect(deepEqual(this.owner.model, _d)).toBe(true);
            });

            it("should reject invalid data and leave model pristine", () => {
                _d = {
                    name: "Ed Testy",
                    age: 99,
                    active: "123",
                };

                this.owner.model = _d;
                expect(deepEqual(this.owner.model, {})).toBe(true);
            });
        });

        describe("LifeCycle: Update", () => {
            const _d = {
                name: "Ed Testy",
                age: 99,
                active: true,
            };

            beforeEach(() => {
                this.owner.model = _d;
            });

            it("should update with valid data", () => {
                this.owner.model.active = false;
                expect(deepEqual(this.owner.model, _d)).toBe(false);
            });

            it("should reject invalid data update", () => {
                this.owner.model.active = "false";
                expect(deepEqual(this.owner.model, _d)).toBe(true);
            });
        });
    });

    describe("Nested PropertiesModel Tests", () => {
        let _owner;
        beforeEach(() => {
            _owner = new Model({schemas: [nestedModel]});
        });

        describe("LifeCycle: Instantiation", () => {
            it("should initialize a valid schema and a schema object", () => {
                expect(_owner.model.$model).toBeDefined();
                expect(_owner.model.$model instanceof PropertiesModel).toBe(true);
                expect(_owner.model.$model).toBeDefined();
                expect(_owner.model.$model instanceof PropertiesModel).toBe(true);
            });
        });

        describe("LifeCycle: Create", () => {

            let _d;

            it("should populate with valid data and make that data accessible", () => {
                _d = {
                    aObject: {
                        bObject: {
                            bValue: 1234,
                        },
                    },
                };

                _owner.model = _d;
                expect(deepEqual(_owner.model, _d)).toBe(true);
                expect(_owner.model.aObject.bObject.$model).toBeDefined();
                expect(_owner.model.aObject.bObject.$model instanceof PropertiesModel).toBe(true)
            });

            it("should reject invalid data and leave model pristine", () => {
                _d = {
                    aObject: {
                        bObject: {
                            bValue: "1234",
                        },
                    },
                };

                _owner.model = _d;
                expect(deepEqual(_owner.model, {})).toBe(true);
            });
        });

        describe("LifeCycle: Update", () => {

            let _d;

            it("should updated nested property models with valid data and pass validation", () => {
                _d = {
                    aObject: {
                        bObject: {
                            bValue: 1234,
                        },
                    },
                };

                _owner.model = _d;

                _d = {
                    bValue: 4321,
                };

                _owner.model.aObject.bObject = _d;
                expect(_owner.errors).toBe(null);
                expect(deepEqual(_owner.model.aObject.bObject, _d)).toBe(true);
            });
        });

        describe("LifeCycle: Delete", () => {

            let _d = {
                aObject: {
                    bObject: {
                        bValue: 1234,
                    },
                },
                extraObject: {
                    someValue: "test",
                },
            };

            it("should allow deletion of nested properties that are not required", () => {
                _owner.model = _d;
                expect(_owner.model.extraObject.someValue).toBe("test");
                delete _owner.model.extraObject.someValue;
                expect(_owner.errors).toBe(null);
                expect(_owner.model.extraObject.someValue).toBe(void(0));
            });

            it("should prevent deletion of nested properties that are required", () => {
                _owner.model = _d;
                expect(_owner.model.aObject.bObject.bValue).toBe(1234);
                delete _owner.model.aObject.bObject.bValue;
                expect(typeof _owner.errors).toBe("object");
                expect(_owner.model.aObject.bObject.bValue).toBe(1234);
            });
        });
    });

    describe("Nested Defaults PropertiesModel Tests", () => {
        let _owner;
        beforeEach(() => {
            _owner = new Model({schemas: [nestedModelDefault]}, {ajvOptions: {useDefaults: true}});
        });

        it("should populate default values", () => {
            const _d = {
                aObject: {
                    bObject: {},
                },
            };

            _owner.model = _d;
            expect(_owner.model.aObject.bObject.bValue).toBe(123);
        });
    });

    describe("Pattern Properties", () => {
        let _owner;
        beforeEach(() => {
            _owner = new Model({schemas: [patternModel]}, {ajvOptions: {useDefaults: true}});
        });

        it("should allow pattern properties", () => {
            _owner.model["test"] = "foo";
            expect(`${_owner}`).toEqual("{}");
            _owner.model["name"] = "foo";
            expect(`${_owner}`).toEqual("{\"name\":\"foo\"}");
        });

        it("should accept multiple uses of patternProperty", () => {
            _owner.model["name"] = "foo";
            expect(`${_owner}`).toEqual("{\"name\":\"foo\"}");
            _owner.model["name"] = "bar";
            expect(`${_owner}`).toEqual("{\"name\":\"bar\"}");
        });

    });

    describe("Nested Pattern Properties", () => {
        let _owner;
        beforeEach(() => {
            _owner = new Model({schemas: [nestedPatternModel]}, {ajvOptions: {useDefaults: true}});
        });

        it("should allow nested pattern properties", () => {
            _owner.model = {name: "test", nested: {test1: {foo: "bar"}}};
            expect(`${_owner.model.nested.$model}`).toEqual("{\"test1\":{\"foo\":\"bar\"}}");
        });

        it("should accept multiple uses of patternProperty", () => {
            _owner.model = {name: "test", nested: {test1: {foo1: "bar1"}}};
            expect(`${_owner.model.nested.$model}`).toEqual("{\"test1\":{\"foo1\":\"bar1\"}}");
            _owner.model.nested["test2"] = {foo2: "bar2"};
            expect(`${_owner.model.nested.$model}`).toEqual("{\"test1\":{\"foo1\":\"bar1\"},\"test2\":{\"foo2\":\"bar2\"}}");
        });

        it("should reject subsequent invalid uses of patternProperty", () => {
            _owner.model = {name: "test", nested: {test1: {foo1: "bar1"}}};
            _owner.model.nested["test1"] = false;
            expect(_owner.errors === null).toBe(false);
            expect(`${_owner.errors[0].dataPath}`).toEqual("/test1");
            expect(`${_owner.errors[0].message}`).toEqual("should be object");
        });

    });
});

