import {wrapCallback} from "./Utils";

export class Executor {
    /**
     *
     * @param callbacks
     * @param data
     * @returns {*}
     * @private
     */
    static exec(callbacks, data) {
        let _value;
        const _it = _cbIterator(callbacks);
        let _done = false;
        _value = data;
        while (!_done) {
            let {done, value} = _it.next(_value);
            if (!(_done = done)) {
                _value = value;
            }
        }

        return _value;
    };
}

/**
 * Promise-safe callback iterator for pipeline transactions
 * @param callbacks
 * @returns {{next: (function(*=): *)}}
 * @private
 */
const _cbIterator = (callbacks) => {
    let _idx = -1;
    if (Array.isArray(callbacks[0])) {
        callbacks = callbacks[0];
    }

    return {
        next: (data) => {
            return (_idx++ < (callbacks.length - 1)) ? {
                value: (wrapCallback(callbacks[_idx] || ((__) => __)))(data),
                done: false,
            } : {
                value: data || false,
                done: true,
            };
        },
    };
};

import {Pipeline} from "./Pipeline";

const _iterators = new WeakMap();
const _pipes = new WeakMap();

export class Iterator {
    constructor(...pipesOrSchemas) {
        const _pipe = new Pipeline(...pipesOrSchemas);
        _pipes.set(this, _pipe);
        _iterators.set(this, [...pipesOrSchemas]);
    }

    get schema() {
        return _pipes.get(this).schema;
    }

    loop(records) {
        if (!Array.isArray(records)) {
            throw {
                error: {
                    message: "iterators accept iterable values only"
                },
                data: records,
            }
        }

        let _res = [];
        records.forEach(
            (__) => {
                const _it = _pipes.get(this).yield(__);
                let _done = false;
                let _value = __;
                while (!_done) {
                    try {
                        let {done, value} = _it.next(_value);
                        if (!(_done = done)) {
                            _value = value;
                        }
                    } catch (e) {
                        _value = void 0;
                    }
                }

                if (_value !== void 0) {
                    _res[_res.length] = _value;
                }
            });

        return _res;
    }
}

