    });
    it("should error on value that are not iterable", () => {
        const __ = new Iterator((_) => _ * 2);
        expect(() => __.loop({0: 1, 1: 3, 2: 5})).toThrow();
    });
    it("should iterate a schema", () => {
        const __ = (
            new Iterator({type: "number"})
